export const getYearMonthDay=t=>{if(!t)return"";const e=new Date(t);return{year:e.getFullYear(),month:e.getMonth()+1,day:e.getDate()}};export const getDate=(t,e,n)=>t&&e&&n?new Date(t,e-1,n):new Date;const compareDate=(t,e,n)=>{const a=new Date(t),o=new Date(e),r=new Date(a.toLocaleDateString()),c=new Date(o.toLocaleDateString());return{Equal:r.getTime()==c.getTime(),EqAndLt:r<=c,EqAndGt:r>=c,Lt:r<c,Gt:r>c}[n]};export const isEqual=(t,e)=>compareDate(t,e,"Equal");export const isEqAndLt=(t,e)=>compareDate(t,e,"EqAndLt");export const isEqAndGt=(t,e)=>compareDate(t,e,"EqAndGt");export const isLt=(t,e)=>compareDate(t,e,"Lt");export const isGt=(t,e)=>compareDate(t,e,"Gt");export const isCurrentMonth=(t,e)=>{const n=new Date(t),a=new Date(e);return n.getFullYear()==a.getFullYear()&&n.getMonth()==a.getMonth()};