import{getSystemInfoSync}from"./utils";function compareVersion(e,n){e=e.split("."),n=n.split(".");const t=Math.max(e.length,n.length);for(;e.length<t;)e.push("0");for(;n.length<t;)n.push("0");for(let r=0;r<t;r++){const t=parseInt(e[r],10),o=parseInt(n[r],10);if(t>o)return 1;if(t<o)return-1}return 0}export function canIUseModel(){return compareVersion(getSystemInfoSync().SDKVersion,"2.9.3")>=0}export function canIUseFormFieldButton(){return compareVersion(getSystemInfoSync().SDKVersion,"2.10.3")>=0}export function canIUsePreviewMedia(){return compareVersion(getSystemInfoSync().SDKVersion,"2.12.0")>=0}